  @override
  void initState() { 
    userid = FirebaseAuth.instance.currentUser.uid.toString();
      FirebaseDatabase.instance.reference().child('users').child("$userid")
      .once().
      then((DataSnapshot snapshot){
        var value = snapshot.value;
        if(value != null)
        {
          
          avatar = value["avatar"].toString();
        }
      });

    super.initState();

      }
    @override
  void dispose() {
    super.dispose();
  }
  void setAvatar(var str)
  { 
      FirebaseDatabase.instance.reference().child('users').child("$userid")
      .update(
        {
          "avatar" : str
        }
      );
      setState(() {
            avatar = str;

      });
      Navigator.pop(context);
  }
  void changeAvatar()
  {
    showDialog(
      context: context,
      builder: (context) => 
      AlertDialog(
                  scrollable: true,
                  title: Text(
                    "Choose A New Avatar",
                    style: TextStyle(
                        color: Colors.white,
                        fontWeight: FontWeight.w200,
                        fontSize: 17),
                  ),
                  backgroundColor: FitnessAppTheme.darkBackground,
                  content: 
                  Container(
                    width: 500,
                    height: 900,
                    child: GridView(
      children: List<Widget>.generate(
        avatarListData.length,
        (int index) {
          
          return InkWell(
            onTap: (){
              setAvatar(avatarListData[index].toString());
              },
            child:Container(
            margin: const EdgeInsets.all(5.0),
            decoration: BoxDecoration(
              border: Border.all(color: FitnessAppTheme.nearlyDark),
              borderRadius: BorderRadius.all(
                      Radius.circular(10.0) //                 <--- border radius here
              ),            
              ),
            child:CachedNetworkImage(
            imageUrl: avatarListData[index],
            width: 75,
            height: 75,
            placeholder: (context,url) => SizedBox(width: 50, height: 50, child: CircularProgressIndicator()),
            errorWidget: (context,url,error) => new Icon(Icons.error),
            )
            )
          );
        },
      ),
      gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
        crossAxisCount: 3,
        mainAxisSpacing: 24.0,
        crossAxisSpacing: 24.0,
        childAspectRatio: 1.0,
      ),
    ),
                    ),
                  ),
    );
  }


